con:configuration.scriptText:
//XMLHolder import
import com.eviware.soapui.support.XmlHolder

//Create XMLHolder object
def holder = new XmlHolder(messageExchange.responseContentAsXml)

//Node exists
assert holder["//SubtractResult"] != null

con:config.script:
// Create the XmlHolder object for the response of the request
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
//Get request
def reqHolder = groovyUtils.getXmlHolder( "Add Request#Request" )

//Modify the request parameters
reqHolder["//tem:intA"] = 50
reqHolder["//tem:intB"] = 90

//Update request
reqHolder.updateProperty()

//Get response
def resHolder1 = groovyUtils.getXmlHolder( "Add Request#Response" )

//Run the test step
testRunner.runTestStepByName("Add Request")

//Print response value
log.info "Result: ${resHolder1['//ns1:AddResult']}"

